import React, { Component } from "react";

//cc shortcut is for creating a class component
//sfc short is fo creating a stateless functional component

class NavBar extends Component {
  //In this NavBar comp, we only have single method(render()) and we
  //have no state, we getting all data by a props, we can covert this comp
  //into STATELESS FUNCTIONAL COMPONENT (Instead of using a class we gonna use a function)
  render() {
    return (
      <nav className="navbar navbar-light bg-light">
        <a className="navbar-brand" href="#">
          Navbar{" "}
          <span className="badge badge-pill badge-secondary">
            {this.props.totalCounters}
          </span>
        </a>
      </nav>
    );
  }
}

export default NavBar;
