import React, { Component } from "react";
import NavBar from "./components/navbar";
import Counters from "./components/counters";
import "./App.css";

class App extends Component {
  // Lifted the stateUp from counters component
  //so app com can be the main parent
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };
  // The down below are METHODS that mutate the above STATE:
  handleIncrement = (counter) => {
    const counters = [...this.state.counters];
    const index = counters.indexOf(counter);
    counters[index] = { ...counter };
    counters[index].value++;
    this.setState({ counters });
  };

  handleReset = () => {
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters: counters });
  };

  handleDelete = (counterId) => {
    const counters = this.state.counters.filter((c) => c.id !== counterId);
    this.setState({ counters: counters });
  };

  render() {
    return (
      // The reason we wrap React.Fragment is that we are returning multiple root elements from the render method
      <React.Fragment>
        <NavBar />
        <main className="container">
          {/* Modified Counters comp onReset, onDelete and so on */}
          <Counters
            // Here we should pass the list of counters
            //this.state.counters
            counters={this.state.counters}
            onReset={this.handleReset}
            onIncrement={this.handleIncrement}
            onDelete={this.handleDelete}
          />
        </main>
      </React.Fragment>
    );
  }
}

export default App;

/*Because there is no parent and child relationship between two component
and you want to share data with them, WE NEED TO LIFT THE STATE
UP to the main parent which app component so the other component 
can have same parent, and the app component, we gonna have
the state so we can pass in all the children using PROPS*/
