import React, { Component } from "react";

class Counter extends Component {
  //Here I removed state property (remove local state):
  // Also removed handleIncrement method since we remove the local state
  render() {
    return (
      <div>
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button
          // onClick here we gonna reference to a method in the props object:
          onClick={() => this.props.onIncrement(this.props.counter)}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        {/* Adding delete button to our Component */}
        <button
          // this.props.id to this.props.counter.id because we don't have id anymore
          onClick={() => this.props.onDelete(this.props.counter.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
      </div>
    );
  }

  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    // this.state.value updated into this.props.counter.value (We no longer have state property)
    classes += this.props.counter.value === 0 ? "warning" : "primary";
    return classes;
  }

  formatCount() {
    // this.state.value updated into this.props.counter (We no longer have state property)
    const { value } = this.props.counter;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;
