import React, { Component } from "react";

//cc shortcut is for creating a class component
//sfc short is fo creating a stateless functional component

// Stateless Functional Component
// Instead of using props as a param, we use object deconstructing
const NavBar = (totalCounters) => {
  return (
    <nav className="navbar navbar-light bg-light">
      <a className="navbar-brand" href="#">
        Navbar{" "}
        <span className="badge badge-pill badge-secondary">
          {/* this.props only works with class comp, we need to add props as a param in this func and remove the keyword THIS*/}
          {/* Instead of using props. every single references to the props, we can use destructing object in the above props argument above  */}
          {totalCounters}
        </span>
      </a>
    </nav>
  );
};

export default NavBar;

