// This component, we wanna render a list of counters:
import React, { Component } from "react";
import Counter from "./removing_local_state";

class Counters extends Component {
  // Instead of hard coding the Counter component,
  //we can add array of counter object to our state property
  //and render them using map() method:
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
    ],
  };

  //Create handleIncrement method after removing local state:
  handleIncrement = (counter) => {
    console.log(counter);
  };

  // Create reset handler method just like handle Delete method:
  handleReset = () => {
    const counters = this.state.counters.map((c) => {
      c.value = 0;
      return c;
    });
    this.setState({ counters: counters });
  };

  // This is the rasing and handling delete event in th counter component
  //To update the delete state we need to add counterId param
  handleDelete = (counterId) => {
    //To update the state, we gonna create new array without a given above counter and then call setState component so react can update the satet
    const counters = this.state.counters.filter((c) => c.id !== counterId);
    // The above code created new array:
    //Overide counters object with the above counters const:
    this.setState({ counters: counters }); //If key and value are same simplify to only one object
  };

  render() {
    return (
      <div>
        {/* Add a button for resetting all the counters */}
        <button
          onClick={this.handleReset}
          className="btn btn-primary btn-sm m-2"
        >
          Reset
        </button>

        {this.state.counters.map((counter) => (
          // Added this line (value={this.counter} selected={true}) to use props
          <Counter
            key={counter.id}
            onDelete={this.handleDelete}
            onIncrement={this.handleIncrement}
            // To simplify adding new property to our counter object in the future, we need to simplify like this:
            counter={counter}
            //The above code took care of all the below ones:
            // value={counter.value}
            // selected={counter.selected}
            // selected={true}
            // id={counter.id}
          />
        ))}
      </div>
    );
  }
}

export default Counters;

/*The difference between Props and state:
Props include data that we give component 
whereas state includes data the is local or private to that component 
so the other components can't access that state, 
it's completely internal to that component only.
Another point is that props are immutable, they are read only, can't be changed*/
