import React, { Component } from "react";
import Counter from "./counter";

//cc shortcut is for creating a class component
//sfc short is fo creating a stateless functional component

class Counters extends Component {
  render() {
    return (
      <div>
        <button
          // this.handleReset method is no longer exist in this class, no it's part of its parent (app com)
          // this.handleReset changed into this.props.onReset
          onClick={this.props.onReset}
          className="btn btn-primary btn-sm m-2"
        >
          Reset
        </button>
        {/* Since we no longer have state in this comp, we need to pass the list of counters by props */}
        {/* this.state.counters becomes this.props.counters */}
        {this.props.counters.map((counter) => (
          <Counter
            key={counter.id}
            // onDelete={this.handleDelete}
            // onIncrement={this.handleIncrement}
            //Updated the above references because Counter comp
            //raises onDel and onInc events, and we are bubbling to app comp(parent)
            //We aren't handling this event, we simply bubbling to its parent(app comp)
            onDelete={this.props.onDelete}
            onIncrement={this.props.onIncrement}
            counter={counter}
          />
        ))}
      </div>
    );
  }
}

export default Counters;
