import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    // imageUrl: "https://picsum.photos/200",
  };

  //   styles = {
  //     fontSize: 20,
  //     fontWeight: "bold",
  //   };
  render() {
    return (
      // React.Fragment replaces div that is not doing anything
      //   <React.Fragment>
      <div>
        {/* Setting attributes in the elements */}
        {/* <img src={this.state.imageUrl} alt="" /> */}
        {/* style={this.styles} goes inside the span style={{fontSize: 30}} */}
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button className="btn btn-secondary btn-sm">Increment</button>
      </div>
      //   </React.Fragment>
    );
  }
  // Create a method that changes the color of the badge depend on the counter:
  getBadgeClasses() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }

  // Create a method here:
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
    // We call also return JSX expression:
    // return count === 0 ? <h4>Zero</h4> : count;
  }
}

export default Counter;
